--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "INVENICT"."DECRYPT" (oldpass VARCHAR2) RETURN VARCHAR2 IS
ENCRYPT_PASS VARCHAR2(30);
PASSWORD  VARCHAR2(30);
String_length  NUMBER;
Char_ascii NUMBER;
Rand_key_no NUMBER;
ctr  NUMBER;
BEGIN
ctr:=1;
ENCRYPT_PASS:=oldpass;
SELECT ASCII(SUBSTR(ENCRYPT_PASS,1,1)) INTO Rand_key_no FROM DUAL;
string_length:=LENGTH(ENCRYPT_PASS);
FOR ctr IN 2..string_length LOOP
 SELECT ASCII(SUBSTR(ENCRYPT_PASS,ctr,1)) INTO char_ascii FROM DUAL;
 char_ascii:=char_ascii-rand_key_no-ctr+1;
 IF char_ascii<0 THEN
  Char_ascii:=Char_ascii+255;
 END IF;
 PASSWORD:=PASSWORD||CHR(char_ascii);
END LOOP;
RETURN(PASSWORD);
END;


/
--------------------------------------------------------
--  DDL for Function ENCRYPT
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "INVENICT"."ENCRYPT" (val_in CHAR)
RETURN VARCHAR2
IS
PASSWORD  VARCHAR2(30);
String_length  NUMBER;
Char_ascii NUMBER;
Rand_key_no NUMBER;
ctr  NUMBER;
retval          VARCHAR2(50);
BEGIN
  ctr:=1;
  SELECT TO_CHAR(SYSDATE,'ss') INTO rand_key_no FROM dual;
  string_length:=LENGTH(val_in);
  FOR ctr IN 1..string_length LOOP
 SELECT ASCII(SUBSTR(val_in,ctr,1)) INTO char_ascii FROM DUAL;
 char_ascii:=char_ascii+rand_key_no+ctr;
 IF char_ascii>255 THEN
  Char_ascii:=Char_ascii-255;
 END IF;
 PASSWORD:=PASSWORD||CHR(char_ascii);
  END LOOP;
  PASSWORD:=CHR(rand_key_no)||PASSWORD;
  retval:= PASSWORD;
  RETURN retval;
END;


